import { Accessor } from 'solid-js';
import { UseTimeoutFnOptions } from '../useTimeoutFn/index.js';
import { Fn, Stoppable } from '../utils/types.js';
import 'solid-js/types/reactive/signal';

interface UseTimeoutOptions<Controls extends boolean> extends UseTimeoutFnOptions {
    /**
     * Expose more controls
     *
     * @default false
     */
    controls?: Controls;
    /**
     * Callback on timeout
     */
    callback?: Fn;
}
/**
 * Update value after a given time with controls.
 */
declare function useTimeout(interval?: number, options?: UseTimeoutOptions<false>): Accessor<boolean>;
declare function useTimeout(interval: number, options: UseTimeoutOptions<true>): {
    ready: Accessor<boolean>;
} & Stoppable;

export { UseTimeoutOptions, useTimeout };
