import { MaybeAccessor, Stoppable } from '../utils/types.js';
import 'solid-js';
import 'solid-js/types/reactive/signal';

interface UseTimeoutFnOptions {
    /**
     * Start the timer immediate after calling this function
     *
     * @default true
     */
    immediate?: boolean;
}
/**
 * Wrapper for `setTimeout` with controls.
 */
declare function useTimeoutFn<CallbackFn extends (...args: any[]) => any>(cb: CallbackFn, interval: MaybeAccessor<number>, options?: UseTimeoutFnOptions): Stoppable<Parameters<CallbackFn> | []>;

export { UseTimeoutFnOptions, useTimeoutFn };
