import { Signal, Accessor } from 'solid-js';
import { Pausable } from '@solidjs-use/shared';

interface UseTimestampOptions<Controls extends boolean> {
    /**
     * Expose more controls
     *
     * @default false
     */
    controls?: Controls;
    /**
     * Offset value adding to the value
     *
     * @default 0
     */
    offset?: number;
    /**
     * Update the timestamp immediately
     *
     * @default true
     */
    immediate?: boolean;
    /**
     * Update interval, or use requestAnimationFrame
     *
     * @default requestAnimationFrame
     */
    interval?: 'requestAnimationFrame' | number;
    /**
     * Callback on each update
     */
    callback?: (timestamp: number) => void;
}
/**
 * Reactive current timestamp.
 */
declare function useTimestamp(options?: UseTimestampOptions<false>): Signal<number>;
declare function useTimestamp(options: UseTimestampOptions<true>): {
    timestamp: Accessor<number>;
} & Pausable;
type UseTimestampReturn = ReturnType<typeof useTimestamp>;

export { UseTimestampOptions, UseTimestampReturn, useTimestamp };
