import { Signal } from 'solid-js';

interface SyncSignalOptions<L, R = L> {
    /**
     * Sync values immediately
     */
    defer?: boolean;
    /**
     * Direction of syncing. Value will be redefined if you define syncConvertors
     *
     * @default 'both'
     */
    direction?: 'ltr' | 'rtl' | 'both';
    /**
     * Custom transform function
     */
    transform?: {
        ltr?: (left: L) => R;
        rtl?: (right: R) => L;
    };
}
/**
 * Two-way signal synchronization.
 *
 * @param left
 * @param right
 */
declare function syncSignal<L, R = L>([left, setLeft]: Signal<L>, [right, setRight]: Signal<R>, options?: SyncSignalOptions<L, R>): () => void;

export { SyncSignalOptions, syncSignal };
